package net.linxingyang.book.vo;import java.util.HashSet;import java.util.Set;import net.linxingyang.book.pojo.Booklist;import net.linxingyang.book.pojo.BooklistConfig;import net.linxingyang.common.utils.DateUtils;import net.linxingyang.config.pojo.Config;public class BooklistVo extends Booklist {		private Set<BooklistConfig> booklistConfigs = new HashSet<BooklistConfig>();	private Set<Config> configs = new HashSet<Config>();	    public String getCreateDateStr() {        return DateUtils.getDateStanderdStr(getCreateDate());    }    public String getModifyDateStr() {        return DateUtils.getDateStanderdStr(getModifyDate());    }	public Set<BooklistConfig> getBooklistConfigs() {		return booklistConfigs;	}	public void setBooklistConfigs(Set<BooklistConfig> booklistConfigs) {		this.booklistConfigs = booklistConfigs;	}    	public Set<Config> getConfigs() {		return configs;	}	public void setConfigs(Set<Config> configs) {		this.configs = configs;	}	public String getTagNames() {		if (0 == configs.size()) {			return "";		} else {			StringBuilder tagNames = new StringBuilder();			for (Config config : configs) {				tagNames.append("[");				tagNames.append(config.getPropertyValue());				tagNames.append("]");			}			return tagNames.toString();		}			}        }