package net.linxingyang.diary.controller;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import net.linxingyang.common.vo.CommonVo;import net.linxingyang.common.vo.ResponseCode;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import net.linxingyang.common.utils.LightPageBean;import net.linxingyang.diary.pojo.WeekTask;import net.linxingyang.diary.vo.WeekTaskVo;import net.linxingyang.diary.qo.WeekTaskQo;import net.linxingyang.diary.service.WeekTaskService;import net.linxingyang.diary.exception.DiaryException;@Controller@RequestMapping(value="weekTask")public class WeekTaskController {    private static Logger log = LoggerFactory.getLogger(WeekTaskController.class);    @Autowired    private WeekTaskService weekTaskService;    /**     * 查询周计划列表     *      * @param qo     * @return     */    @RequestMapping("/listWeekTask")    public @ResponseBody LightPageBean<WeekTaskVo> listWeekTask(WeekTaskQo qo) {        LightPageBean<WeekTaskVo> vo = new LightPageBean<WeekTaskVo>();                 try {            vo = weekTaskService.selectByQo2(qo);            vo.setCode(ResponseCode.SUCCESS);            vo.setMsg("查询周计划成功!");        } catch (DiaryException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMsg(e.getMessage());            log.info("查询周计划失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMsg("内部错误!");            log.info("查询周计划失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }    /**     * 查询周计划     *      * @param id      * @return     */    @RequestMapping("/queryWeekTask")    public @ResponseBody CommonVo queryWeekTask(Integer id) {        CommonVo vo = new CommonVo();                try {            vo.setResultObject(weekTaskService.selectVoById(id));            vo.setCode(ResponseCode.SUCCESS);            vo.setMessage("查询周计划成功!");        } catch (DiaryException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMessage(e.getMessage());            log.info("查询周计划失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMessage("内部错误!");            log.info("查询周计划失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }        /**     * 更新周计划     *      * @param weekTask     * @return     */    @RequestMapping("/modifyWeekTask")    public @ResponseBody CommonVo modifyWeekTask(WeekTask weekTask) {        CommonVo vo = new CommonVo();                try {            weekTaskService.update(weekTask);            vo.setCode(ResponseCode.SUCCESS);            vo.setMessage("更新周计划成功!");            log.info("更新周计划成功");        } catch (DiaryException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMessage(e.getMessage());            log.info("更新周计划失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMessage("内部错误!");            log.info("更新周计划失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }    /**     * 添加周计划     *      * @param weekTask 周计划     * @return CommonVo 处理结果     */    @RequestMapping("/addWeekTask")    public @ResponseBody CommonVo addWeekTask(WeekTask weekTask) {        CommonVo vo = new CommonVo();                try {            weekTaskService.insert(weekTask);            vo.setCode(ResponseCode.SUCCESS);            vo.setMessage("添加周计划成功!");            log.info("添加周计划成功");        } catch (DiaryException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMessage(e.getMessage());            log.info("添加周计划失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMessage("内部错误!");            log.info("添加周计划失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }}