package net.linxingyang.diary.vo;import net.linxingyang.common.utils.DateUtils;import net.linxingyang.diary.pojo.FutureTask;public class FutureTaskVo extends FutureTask {    public String getCreateDateStr() {        return DateUtils.getDateStanderdStr(getCreateDate());    }    public String getModifyDateStr() {        return DateUtils.getDateStanderdStr(getModifyDate());    }    public String getBeginDateStr() {    	return DateUtils.getDateStanderdStr(getBeginDate());    }    public String getEndDateStr() {    	return DateUtils.getDateStanderdStr(getEndDate());    }    public String getStateStr() {    	if (null == getState()) {    		return "";    	}    	switch (getState()) {			case 0:				return "未开始";			case 1:				return "进行中";			case 2:				return "完成";		}    	return "";    }}