package net.linxingyang.diary.vo;import net.linxingyang.common.utils.DateUtils;import net.linxingyang.diary.pojo.Task;public class TaskVo extends Task {    public String getCreateDateStr() {        return DateUtils.getDateStanderdStr(getCreateDate());    }    public String getModifyDateStr() {        return DateUtils.getDateStanderdStr(getModifyDate());    }        public String getDiscardStr() {    	if (null == getDiscard()) {    		return "";    	} else {    		if (1 == getDiscard()) {    			return "停用";    		} else {    			return "在用";    		}    	}    }    }