package net.linxingyang.personal.controller;import net.linxingyang.common.utils.LightPageBean;import net.linxingyang.common.vo.CommonVo;import net.linxingyang.common.vo.ResponseCode;import net.linxingyang.personal.exception.PersonalException;import net.linxingyang.personal.pojo.LongTermTask;import net.linxingyang.personal.qo.LongTermTaskQo;import net.linxingyang.personal.service.LongTermTaskService;import net.linxingyang.personal.vo.LongTermTaskVo;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;@Controller@RequestMapping(value="longTermTask")public class LongTermTaskController {    private static Logger log = LoggerFactory.getLogger(LongTermTaskController.class);    @Autowired    private LongTermTaskService longTermTaskService;    /**     * 查询突然想法列表     *      * @param qo     * @return     */    @RequestMapping("/listLongTermTask")    public @ResponseBody LightPageBean<LongTermTaskVo> listLongTermTask(LongTermTaskQo qo) {        LightPageBean<LongTermTaskVo> vo = new LightPageBean<LongTermTaskVo>();                 try {            vo = longTermTaskService.selectByQo2(qo);            vo.setCode(ResponseCode.SUCCESS);            vo.setMsg("查询突然想法成功!");        } catch (PersonalException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMsg(e.getMessage());            log.info("查询突然想法失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMsg("内部错误!");            log.info("查询突然想法失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }    /**     * 查询突然想法     *      * @param id      * @return     */    @RequestMapping("/queryLongTermTask")    public @ResponseBody CommonVo queryLongTermTask(Integer id) {        CommonVo vo = new CommonVo();                try {            vo.setResultObject(longTermTaskService.selectVoById(id));            vo.setCode(ResponseCode.SUCCESS);            vo.setMessage("查询突然想法成功!");        } catch (PersonalException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMessage(e.getMessage());            log.info("查询突然想法失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMessage("内部错误!");            log.info("查询突然想法失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }        /**     * 更新突然想法     *      * @param longTermTask     * @return     */    @RequestMapping("/modifyLongTermTask")    public @ResponseBody CommonVo modifyLongTermTask(LongTermTask longTermTask) {        CommonVo vo = new CommonVo();                try {            longTermTaskService.update(longTermTask);            vo.setCode(ResponseCode.SUCCESS);            vo.setMessage("更新突然想法成功!");            log.info("更新突然想法成功");        } catch (PersonalException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMessage(e.getMessage());            log.info("更新突然想法失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMessage("内部错误!");            log.info("更新突然想法失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }    /**     * 添加突然想法     *      * @param longTermTask 突然想法     * @return CommonVo 处理结果     */    @RequestMapping("/addLongTermTask")    public @ResponseBody CommonVo addLongTermTask(LongTermTask longTermTask) {        CommonVo vo = new CommonVo();                try {            longTermTaskService.insert(longTermTask);            vo.setCode(ResponseCode.SUCCESS);            vo.setMessage("添加突然想法成功!");            log.info("添加突然想法成功");        } catch (PersonalException e) {            vo.setCode(ResponseCode.ERROR_INPUT);            vo.setMessage(e.getMessage());            log.info("添加突然想法失败：" + vo.getCode() + ":" + e.getMessage());        } catch (Exception e) {            vo.setCode(ResponseCode.INTERNAL_ERROR);            vo.setMessage("内部错误!");            log.info("添加突然想法失败：" + vo.getCode() + "：" + e.getMessage());        }        return vo;    }}