package net.linxingyang.personal.vo;import net.linxingyang.common.utils.DateUtils;import net.linxingyang.personal.pojo.BigThing;public class BigThingVo extends BigThing {    public String getCreateDateStr() {        return DateUtils.getDateStanderdStr(getCreateDate());    }    public String getModifyDateStr() {        return DateUtils.getDateStanderdStr(getModifyDate());    }    public String getThingDateStr() {    	if (null == getThingDate()) {    		return "";    	} else {    		return DateUtils.getDateStanderdStr(getThingDate());    	}    }    public String getTypeStr() {    	if (null == getType()) {    		return "";    	}     	if (0 == getType()) {    		return "关于我";    	}    	if (1 == getType()) {    		return "认识的人";    	}    	return "社会上";    }}