package net.linxingyang.personal.vo;import net.linxingyang.common.utils.DateUtils;import net.linxingyang.personal.pojo.Person;public class PersonVo extends Person {    public String getCreateDateStr() {        return DateUtils.getDateStanderdStr(getCreateDate());    }    public String getModifyDateStr() {        return DateUtils.getDateStanderdStr(getModifyDate());    }     public String getSexStr() {    	if (null == getSex()) {    		return "";    	}     	if (1 == getSex()) {    		return "男";    	}    	return "女";    }        private boolean existsPeopleHistory = false;	public boolean isExistsPeopleHistory() {		return existsPeopleHistory;	}	public void setExistsPeopleHistory(boolean existsPeopleHistory) {		this.existsPeopleHistory = existsPeopleHistory;	}    public String getAddOrModify() {    	if (existsPeopleHistory) {    		return "修改人物小传";    	} else {    		return "添加人物小传";    	}    }    }