package net.linxingyang.personal.vo;import net.linxingyang.common.utils.DateUtils;import net.linxingyang.personal.pojo.PersonWord;public class PersonWordVo extends PersonWord {    public String getCreateDateStr() {        return DateUtils.getDateStanderdStr(getCreateDate());    }    public String getModifyDateStr() {        return DateUtils.getDateStanderdStr(getModifyDate());    }        public String getWriteDateStr() {    	if (null == getWriteDate()) {    		return "";    	} else {    		return DateUtils.getDateStanderdStr(getWriteDate());    	}    }    }