package net.linxingyang.personal.vo;import net.linxingyang.common.utils.DateUtils;import net.linxingyang.personal.pojo.Word;public class WordVo extends Word {    public String getCreateDateStr() {        return DateUtils.getDateStanderdStr(getCreateDate());    }    public String getModifyDateStr() {        return DateUtils.getDateStanderdStr(getModifyDate());    }        public String getLikePercentStr() {    	if (null == getLikePercent()) {    		return "";    	}    	switch (getLikePercent()) {			case 5:				return "座右铭";			case 4:				return "非常喜欢";			case 3:				return "印象深刻";			case 2:				return "有点感触";			default:				return "老生常谈";		}    }    }